import React, { Component } from "react";

export default class About extends Component {
    state = {
        data: this.props.data
    };

    handleChange = event => {
        this.setState({ data: event.target.value });
    }

    componentWillReceiveProps(nextProps) {
        this.setState({ data: nextProps.data })
    }

    updateAboutMe = event => {
        event.preventDefault();

        const update = this.state.data;

        this.props.updateAboutMe(update);
    }

    render() {
        return (
            <div>
                <h5 className="font-accident-two-normal uppercase subtitle">Manage about me</h5>

                <div className="col-md-12">
                    <div className="dividewhite2"></div>
                    <div id="form-messages"></div>

                    <form
                        className="wpcf7-form"
                        onSubmit={(event) => this.updateAboutMe(event)}
                    >
                        <div className="field">
                            <textarea
                                value={this.state.data}
                                onChange={this.handleChange}
                            >
                            </textarea>
                        </div>

                        <div className="field text-center">
                            <button type="submit" className="btn btn-lg btn-darker">Update</button>
                        </div>

                    </form>

                </div>
            </div>
        );
    }
}