import React from "react";
import { Link } from "react-router-dom";

export default class SectionHeader extends React.Component {

    toggleSection = section => {
        this.props.toggleSection(section);
    }

    render() {
        const { name, toggled, isEnd } = this.props;

        return (
            <Link to={`/admin/${name}`}>
            <h3
                onClick={() => this.toggleSection(name)}
                className={`uppercase ${!isEnd ? "pull-left" : "" } padding-0-15-0-0 ${toggled ? "dark" : "light"}`}>{name}</h3>
            </Link>
        );
    }
}
