import React from "react";

export default class SectionHeader extends React.Component {

    toggleSection = section => {
        this.props.toggleSection(section);
    }

    render() {
        const { name, toggled } = this.props;
        console.log(toggled);
        return (
            <a><h3
                onClick={() => this.toggleSection(name)}
                className={`uppercase pull-left padding-0-15-0-0 ${toggled ? "dark" : "light"}`}>{name}</h3>
            </a>
        );
    }
}
