import React from "react";
import { Link } from "react-router-dom";

const SectionHeader = ({ name, isEnd, toggled }) => (
    <Link to={`/admin/${name}`}>
        <h3
            className={`uppercase ${!isEnd ? "pull-left" : ""} padding-0-15-0-0 ${toggled ? "dark" : "light"}`}>{name}</h3>
    </Link>
);

export default SectionHeader;