import React from "react";
import PropTypes from "prop-types";

const ContactItem = ({ label, value, link }) => (
	<div className="row">
		<div className="col-sm-2"><span className="font-accident-two-bold uppercase">{`${label}:`}</span></div>
		{value ? <div className="col-sm-10"><p className="small contact-pad">{value}</p></div> : ""}

		{link ? <div className="col-sm-10"><p className="small contact-pad">{link}</p></div> : ""}
	</div>
);

ContactItem.propTypes = {
	label: PropTypes.string.isRequired,
	value: PropTypes.string,
	link: PropTypes.string,
};

ContactItem.defaultProps = {
	label: "",
	value: "",
	link: "",
};

export default ContactItem;
