import React from "react";

const ContactItem = ({ label, value, link }) => (
    <div className="row">
        <div className="col-sm-2"><span className="font-accident-two-bold uppercase">{`${label}:`}</span></div>
        {value
            ? <div className="col-sm-10"><p className="small contact-pad">{value}</p></div>
            : "" }

        {link
            ? <div className="col-sm-10"><p className="small contact-pad">{link}</p></div>
            : "" }
    </div>
);

export default ContactItem;