import React from "react";

const ContactType = ({ route, label, value, origin, duration, delay, distance }) => (
    <a href={route && "#"} className="col-md-12 col-sm-6 col-xs-6 flex-column p-grid-item nopadding">
        <div className="flex-panel dot-block padding-30-30-30-0"
            data-animation-origin={origin && "right"}
            data-animation-duration={duration && "300"}
            data-animation-delay={delay && "400"}
            data-animation-distance={distance && "200px"}>
            <div className="p-icon ui-block-color05"></div>
            <span className="uppercase">{label}</span>
            <p>{value}</p>
        </div>
    </a>
);

export default ContactType;