import React from "react";
import PropTypes from "prop-types";

const ContactType = ({ route, label, value, origin, duration, delay, distance }) => (
	<a href={route} className="col-md-12 col-sm-6 col-xs-6 flex-column p-grid-item nopadding">
		<div
			className="flex-panel dot-block padding-30-30-30-0"
			data-animation-origin={origin}
			data-animation-duration={duration}
			data-animation-delay={delay}
			data-animation-distance={distance}
		>
			<div className="p-icon ui-block-color05" />
			<span className="uppercase">{label}</span>
			<p>{value}</p>
		</div>
	</a>
);

ContactType.propTypes = {
	route: PropTypes.string.isRequired,
	label: PropTypes.string.isRequired,
	value: PropTypes.string.isRequired,
	origin: PropTypes.string,
	duration: PropTypes.number,
	delay: PropTypes.number,
	distance: PropTypes.string,
};

ContactType.defaultProps = {
	route: "",
	label: "",
	value: "",
	origin: "right",
	duration: 300,
	delay: 400,
	distance: "200px",
};

export default ContactType;
