import React from "react";

const InfoTile = ({ origin, duration, delay, distance, label, value }) => (
    <a href="resume.html" className="col-md-6 flex-column p-grid-item nopadding">
        <div className="flex-panel dot-block padding-30-30-30-0"
            data-animation-origin={origin && "left"}
            data-animation-duration={duration && "300"}
            data-animation-delay={delay && "800"}
            data-animation-distance={distance && "50px"}>
            <div className="p-icon ui-block-color05"></div>
            <span className="uppercase">{label}</span>
            <p>{value}</p>
        </div>
    </a>
);

export default InfoTile;