import React from "react";
import PropTypes from "prop-types";

const InfoTile = ({ origin, duration, delay, distance, label, value }) => (
    <a href="resume.html" className="col-md-6 flex-column p-grid-item nopadding">
        <div className="flex-panel dot-block padding-30-30-30-0"
            data-animation-origin={origin}
            data-animation-duration={duration}
            data-animation-delay={delay}
            data-animation-distance={distance}>
            <div className="p-icon ui-block-color05"></div>
            <span className="uppercase">{label}</span>
            <p>{value}</p>
        </div>
    </a>
);

InfoTile.propTypes = {
    orgin: PropTypes.string,
    durration: PropTypes.number,
    delay: PropTypes.number,
    distance: PropTypes.string,
    label: PropTypes.string.isRequired,
    value: PropTypes.string.isRequired,
}

InfoTile.defaultProps = {
    orgin: "right",
    durration: 300,
    delay: 800,
    distance: "50px",
    label: "",
    value: "",
}

export default InfoTile;