import React from "react";
import PropTypes from "prop-types";

import Header from "maiku.net/components/ui/header";
import Footer from "maiku.net/components/ui/footer";

const Layout = props => (
	<span>
		<Header info={props.info} />
		{props.children}
		<Footer />
		<div id="back-top" />
	</span>
);

Layout.propTypes = {
	info: PropTypes.arrayOf(
		PropTypes.shape({
			_id: PropTypes.string,
			value: PropTypes.string,
		}),
	),
    children: PropTypes.element.isRequired,
};

Layout.defaultProps = {
	info: [],
};

export default Layout;
