import React from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

const PageTile = ({ name, icon, link, duration, delay }) => (
	<li
		className="col-xs-4 nopadding menuitem ui-menu-color01"
		data-animation-duration={duration && "1000"}
		data-animation-delay={delay && "100"}
	>
		<Link className="hvr-sweep-to-bottom" to={link}>
			<i className={`flaticon-${icon}`} /><span>{name}</span>
		</Link>
	</li>
);

PageTile.propTypes = {
	duration: PropTypes.number,
	delay: PropTypes.number,
	name: PropTypes.string.isRequired,
	icon: PropTypes.string.isRequired,
	link: PropTypes.string.isRequired,
};

PageTile.defaultProps = {
	duration: 1000,
	delay: 100,
	name: "",
	icon: "",
	link: "",
};

export default PageTile;
