import React from "react";
import { Link } from "react-router-dom";

const PageTile = ({ name, icon, link, duration, delay }) => (
    <li className="col-xs-4 nopadding menuitem ui-menu-color01" 
        data-animation-duration={duration && "1000"} 
        data-animation-delay={delay && "100"}>
        <Link 
            className="hvr-sweep-to-bottom"
            to={link} >
            <i className={`flaticon-${icon}`}></i><span>{name}</span>
        </Link>
    </li>
);

export default PageTile; 