import React from "react";

const ProfilePic = ({origin, duration, delay, url}) => (
    <a href={url && "#"}>
        <div className="col-md-3 vc-photo photo-05"
            data-animation-origin={origin && "right"}
            data-animation-duration={duration && 500}
            data-animation-delay={delay && 300}
            data-animation-distance="0px">&nbsp;</div>
    </a>
);

export default ProfilePic;