import React from "react";
import PropTypes from "prop-types";

const ResumeFact = ({ label, value }) => (
	<div className="row">
		<div className="col-sm-4"><span className="font-accident-two-bold uppercase">{`${label}:`}</span></div>
		<div className="col-sm-8"><p className="">{value}</p></div>
	</div>
);

ResumeFact.propTypes = {
	label: PropTypes.string.isRequired,
	value: PropTypes.string.isRequired,
};

ResumeFact.defaultProps = {
	label: "",
	value: "",
};

export default ResumeFact;
